import mongoose from "mongoose";
import dotenv from "dotenv";
import fs from "fs";
import Movie from "../models/movieModel.mjs";

// Configure dotenv
dotenv.config({ path: "./config.env" });

// Connect to MongoDB
mongoose
  .connect(process.env.CONN_STR)
  .then(() => {
    console.log("Connected to MongoDB");
  })
  .catch((error) => {
    console.log("Error connecting to MongoDB", error);
  });

// Read movies.json file
const movies = JSON.parse(fs.readFileSync("./movies.json", "utf8"));

// Delete existing movie documents from the collection
const deleteMovies = async () => {
  try {
    await Movie.deleteMany();
    console.log("Movies deleted");
  } catch (err) {
    console.log("Error deleting movies", err);
  }
};

// Import movies into the database
const importMovies = async () => {
  try {
    await Movie.create(movies);
    console.log("Movies imported");
  } catch (err) {
    console.log("Error importing movies", err);
  }
};

console.log(process.argv);

